library(shiny)

ui <- fluidPage(
  titlePanel(
    div(HTML("Appendix of <em>Simultaneous estimation and model choice for 
             big discrete time-to-event data with additive predictors</em>"))
  ),

  sidebarLayout(
    sidebarPanel(
      fileInput("rdsfile", "Upload bb4sa_sim_results.rds"),
    ),

    mainPanel(
      h3("Summary of the Dataset"),
      verbatimTextOutput("summary")
    )
  )
)

server <- function(input, output, session) {
  dat <- reactive({
    req(input$rdsfile)
    readRDS(input$rdsfile$datapath)
  })


  output$summary <- renderPrint({
    summary(dat())
  })

}

shinyApp(ui, server)
